package top;

import java.util.HashMap;
import java.util.Map;
import java.util.Set;

/**
 * https://leetcode-cn.com/problems/permutation-in-string/
 */
public class CheckInclusion {

    public static void main(String[] args) {
        String s1 = "";
        String s2 = "";
//        System.out.println(isEqual("adc","cda"));
        System.out.println(checkInclusion(s1, s2));
    }

    public static boolean checkInclusion(String s1, String s2) {
        System.out.println(s1.length());
        System.out.println(s2.length());
        for (int i = 0; i <= s2.length() - s1.length(); i++) {
            if (isEqual(s1, s2.substring(i, i + s1.length()))){
                return true;
            }
        }
        return false;
    }

    public static boolean isEqual(String s1, String s2) {
        if (s1.length() != s2.length()) {
            return false;
        }
        Map<Character, Integer> maps1 = getMap(s1);
        Map<Character, Integer> maps2 = getMap(s2);
        Set<Character> set = maps1.keySet();
        for (Character c : set) {
            if (maps2.get(c) == null){
                return false;
            }else if (maps1.get(c) - maps2.get(c) != 0) {
                return false;
            }
        }
        return true;
    }

    public static Map<Character, Integer> getMap(String s) {
        char[] array = s.toCharArray();
        Map<Character, Integer> map = new HashMap<>();
        for (Character c : array) {
            if (map.get(c) == null) {
                map.put(c, 1);
            } else {
                map.put(c, map.get(c) + 1);
            }
        }
        return map;
    }
}
